import React from 'react';
import {Products} from '../../data';
import './SingleItem.css';

const SingleItem = (props) => {
    //檢查id是否與路徑pid相同
    console.log(props)
    const productId = props.match.params.pid;
    const checkItem = Products.filter(item => item.id === +productId);
    console.log(checkItem);
    return (
        <div className="container">
            <div className="row">
                <div className="col s7">
                    <img src={checkItem[0].image} alt="" className="itemImg"/>
                </div>
                <div className="col s5">
                    <h5>{checkItem[0].productName}</h5>
                    <h6>CAD {checkItem[0].price}</h6>
                    <br/>
                    <p>{checkItem[0].description}</p>
                    <hr/>
                    <h6>SIZE:</h6>
                    {/* size chart for modal */}
                    <button>S</button>
                    <button>M</button>
                    <button>L</button>
                    <button>XL</button>
                </div>
            </div>
            <div className="row">
                <div className="col s12 center">
                    C
                </div>
            </div>
        </div>
    )
}

export default SingleItem;
