import {Products, Location, Cart} from '../../data';

const initState = {
    Products,
    Location,
    Cart
}

const rootReducer = (state = initState, action) => {
    switch(action.type){
        case "ADD_TO_CART":
            const checkItem = state.Cart.map((item) => {
                if(item.length > 1 && item.id === action.payload[0].id){
                    return item.quantity += 1
                }else{
                    return [...state.Cart, action.payload]
                }

            })
            return{
                ...state,
                Cart: checkItem
            };

        case "REMOVE_FROM_CART":
            const filteredItem = state.Cart.filter(item => item.id !== action.payload)
            return{
                ...state,
                Cart: filteredItem
            };

        case "INCREASE_QUANTITY":
            const increaseItem = state.Cart.map((item) => {
                if(item.id === action.payload){
                    item.quantity += 1;
                    item.totalPrice += Number(item.price)
                }
                return item;
            })
            return{
                ...state,
                Cart: increaseItem
            };

        case "DECREASE_QUANTITY":
                const decreaseItem = state.Cart.map((item) => {
                    if(item.id === action.payload){
                        item.quantity -= 1; 
                        item.totalPrice -= Number(item.price)
                    }
                    return item;
                })
                return{
                    ...state,
                    Cart: decreaseItem
                };

        default:
            return state;
    }

}

export default rootReducer;
