import React from 'react';
import {connect} from 'react-redux';
import './Cart.css';

const Cart = ({cart, increase, decrease, removeItem, total}) => {
    if(cart.length === 0){
        return(
            <div className="row container center">
                <h5>You currently have no items in your shopping cart.</h5>
            </div>
        )
    }

    let itemList = cart.map(item => {
        // console.log(item)

        const handleMinusClick = (e) => {
            e.preventDefault();
            decrease(item.id);
            total(item.totalPrice)
        }
        const handleAddClick = (e) => {
            e.preventDefault();
            increase(item.id);
            total(item.totalPrice)
        }

        const handleRemoveClick = (e) => {
            e.preventDefault();
            removeItem(item.id);
        }
        // fix problem - add same item to the cart will show twice
    return (
        <>
        <div className="row container item-name" key={item.id}>
            <div className="col s5 item-detail">
                <img src={item.image} alt={item.productName} className="item-image"/>
                <div className="des-detail">
                    <p>{item.productName}</p>
                    <label>Choose your size</label>
                    <select className="browser-default">
                        <option value="S">S</option>
                        <option value="M">M</option>
                        <option value="L">L</option>
                        <option value="XL">XL</option>
                    </select>
                </div>
            </div>

            <div className="col s3 item-quantity">
                {(item.quantity <= 1) ?
                    <button className="minus" onClick={handleMinusClick} disabled>-</button>
                    :
                    <button className="minus" onClick={handleMinusClick}>-</button>
                }
                <input type="number" min="1" max="10" value={item.quantity} className="item-amount"/>
                {/* <div className="error-msg">
                    {(item.quantity >= 10) ? <div className="red-text">Maximum is 10</div> : null}
                </div> */}
                {(item.quantity >= 10) ?
                    <button className="plus" onClick={handleAddClick} disabled>+</button>
                    :
                    <button className="plus" onClick={handleAddClick}>+</button>
                }
            </div>

            <div className="col s1"></div>
            <div className="col s1">
                <p>${item.totalPrice.toFixed(2).toString()}</p>
            </div>
            <div className="col s1"></div>
            <div className="col s1">
                <button className="item-remove" onClick={handleRemoveClick}>X</button>
            </div>
        </div>
            <div className="row container">
                <div className="col s8"></div>
                <div className="col s4">
                <p>Total: ${item.totalPrice.toFixed(2).toString()}</p>
            </div>
        </div>
    </>
    )})

    return(
        <div className="cart-wrapper">
            <div className="row container">
                <div className="col s5">
                    <p>My Cart</p>
                </div>
                <div className="col s3">
                    <p>QUANTITY</p>
                </div>
                <div className="col s1"></div>
                <div className="col s1">
                    <p>Price</p>
                </div>
                <div className="col s1"></div>
                <div className="col s1">
                    <p>REMOVE</p>
                </div>
            </div>
                {itemList}

        </div>

    )
}

const mapStateToProps = (state) => {
    return{
        cart: state.Cart
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        increase: (id) => dispatch({type: "INCREASE_QUANTITY", payload: id}),
        decrease: (id) => dispatch({type: "DECREASE_QUANTITY", payload: id}),
        removeItem: (id) => dispatch({type: "REMOVE_FROM_CART", payload: id}),
        total: (price) => dispatch({type: "TOTAL_PRICE", payload: price})
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Cart);
