import React from 'react';
import Map from './Map';
import {connect} from 'react-redux';
import './Location.css';

const Location = ({headStore}) => {
    console.log(headStore)
    if(headStore.length === 0){
        return(
            <div className="row container center">
                <h5>Can't locate the store!</h5>
            </div>
        )
    }
    let showStore = headStore.map(item => {
        return(
            <div className="row container">
                <div className="col s5">
                    <img src={item.imgSrc}
                    alt="" style={{width: "350px", height: "350px", margin: "2rem 0"}}/>
                </div>
                <div className="col s2"></div>
                <div className="col s5" style={{margin: "2rem 0"}}>
                    <h4>Storename: {item.name}</h4>
                    <p>Address: {item.address}</p>
                    <p>Phone: {item.phone}</p>
                    <p>Business Hour: {item.businessHour}</p>
                </div>
            </div>
        )
    })
    return (
        <div className="container location-map">
            <Map LatNLng={headStore}/>
            {showStore}
        </div>
    )
}

const mapStateToProps = (state) => {
    return{
        headStore: state.Location
    }
}

export default connect(mapStateToProps)(Location);
