import React from 'react';
import {connect} from 'react-redux';
import './Cart.css';
const Cart = ({cart, increase, decrease}) => {

    const handleMinusClick = (e) => {
        e.preventDefault();
        decrease(cart[0].id)
    }
    const handleAddClick = (e) => {
        e.preventDefault();
        increase(cart[0].id)
    }

    return (
        <div className="cart-wrapper">
        <div className="row container item-name">
            <div className="col s5">
                <p>My Cart</p>
            </div>
            {/* <div className="col s1"></div> */}
            <div className="col s3">
                <p>QUANTITY</p>
            </div>
            <div className="col s1"></div>
            <div className="col s1">
                <p>Price</p>
            </div>
            <div className="col s1"></div>
            <div className="col s1">
                <p>REMOVE</p>
            </div>
        </div>
        {/* <hr/> */}
        <div className="row container item-name">
            <div className="col s5 item-detail">
                <img src={cart[0].image} alt={cart[0].productName} className="item-image"/>
                <div className="des-detail">
                    <p>{cart[0].productName}</p>
                    <p>Size:</p>
                </div>
            </div>
            {/* <div className="col s1"></div> */}
            <div className="col s3 item-quantity">
                <button className="minus" onClick={handleMinusClick}>-</button>
                <input type="number" min="0" max="10" value={cart[0].quantity} className="item-amount"/>
                <button className="plus" onClick={handleAddClick}>+</button>
            </div>
            <div className="col s1"></div>
            <div className="col s1">
                <p>{cart[0].price * cart[0].quantity}</p>
            </div>
            <div className="col s1"></div>
            <div className="col s1">
                <button className="item-remove">X</button>
            </div>
            {/* <hr/> */}
        </div>
        <div className="row container">
                <div className="col s8"></div>
                <div className="col s4">
                    <p>Total:</p>
                </div>
        </div>
    </div>
    )
}

const mapStateToProps = (state) => {
    return{
        cart: state.Cart
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        increase: (id) => dispatch({type: "INCREASE_QUANTITY", payload: id}),
        decrease: (id) => dispatch({type: "DECREASE_QUANTITY", payload: id})
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Cart);
