import React, {useState} from 'react';
import Modal from 'react-modal';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';

import './SingleItem.css';

const customStyles = {
    content : {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)'
    }
};
Modal.setAppElement("#modal-root");

const SingleItem = ({checkItem, addCart}) => {
    //檢查id是否與路徑pid相同
    // const productId = props.match.params.pid;
    // const checkItem = Products.filter(item => item.id === +productId);
    const [openModal, setOpenModal] = useState(false)

    const openModalHandler = () => {
        setOpenModal(true)
    }

    const closeModalHandler = () => {
        setOpenModal(false)
    }

    const handleAddClick = () => {
        addCart(checkItem[0])
    }

    let oneItem = checkItem.map(item => {
        return(
            <div key={item.id}>
            <Modal
                isOpen={openModal}
                onRequestClose={closeModalHandler}
                style={customStyles}
                contentLabel="Size Chart">
                <img src="https://cdn.shopify.com/s/files/1/1380/3157/files/168293_original_1-2_2048x2048.png?v=1474813445" alt=""/>
            </Modal>

            <div className="content-container" key={item.id}>
            <div className="row">
                <div className="col s7">
                    <img src={item.image} alt="" className="itemImg"/>
                    {/* <div className="wrap-arrow-right">
                        <i className="material-icons arrow-right">keyboard_arrow_right</i>
                    </div>
                    <div className="wrap-arrow-left">
                        <i className="material-icons arrow-left">keyboard_arrow_left</i>
                    </div> */}
                </div>
                <div className="col s5 content">
                    <h5>{item.productName}</h5>
                    <h6>CAD ${item.price}</h6>
                    <p className="descript">{item.description}</p>
                    <hr/>
                    <p onClick={openModalHandler} className="size-chart"><i className="material-icons">straighten</i>Size Chart</p>
                    {/* size chart for modal */}
                    <Link to="/cart">
                        <button className="cart-Btn" onClick={()=>handleAddClick()}>ADD TO CART</button>
                    </Link>
                </div>
            </div>
            <div className="row">
                <div className="col s12 center">
                    <h5>Material</h5>
                    <p>{item.material.farbicDetail}</p>
                    <hr/>
                    <h5>Washing Instructions</h5>
                    <p>{item.material.washingInstruction}</p>
                    <p className="red-text">{item.material.warning}</p>
                </div>
            </div>
        </div> 
        </div>
        )
    })

    return (
        <div className="container">
            {oneItem}
        </div>
    )
}

const mapStateToProps = (state, ownProps) => {
    const productId = ownProps.match.params.pid;
    return{
        checkItem: state.Products.filter(list => list.id === +productId)
    }
}

const mapDispatchToProps = (dispatch) => {
    return{
        addCart: (checkItem) => dispatch({type: "ADD_TO_CART", payload: checkItem})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SingleItem);
