import React from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
// import Cart from '../Pages/Cart';
import './SingleItem.css';

const SingleItem = ({checkItem, addCart}) => {
    //檢查id是否與路徑pid相同
    // const productId = props.match.params.pid;
    // const checkItem = Products.filter(item => item.id === +productId);

    const handleAddClick = () => {
        addCart(checkItem[0])
    }

    let oneItem = checkItem.map(item => {
        console.log(item)
    })

    return (
        <div className="container">
            {oneItem}
            <div className="row">
                <div className="col s7">
                    <img src={checkItem[0].image} alt="" className="itemImg"/>
                    {/* <div className="wrap-arrow-right">
                        <i className="material-icons arrow-right">keyboard_arrow_right</i>
                    </div>
                    <div className="wrap-arrow-left">
                        <i className="material-icons arrow-left">keyboard_arrow_left</i>
                    </div> */}
                </div>
                <div className="col s5 content">
                    <h5>{checkItem[0].productName}</h5>
                    <h6>CAD {checkItem[0].price}</h6>
                    <p className="descript">{checkItem[0].description}</p>
                    <hr/>
                    <h6>Size Chart</h6>
                    {/* size chart for modal */}
                    <Link to="/cart">
                        <button className="cart-Btn" onClick={()=>handleAddClick()}>ADD TO CART</button>
                    </Link>
                </div>
            </div>
            <div className="row">
                <div className="col s12 center">
                    <h5>Material</h5>
                    <p>{checkItem[0].material["farbicDetail"]}</p>
                    <hr/>
                    <h5>Washing Instructions</h5>
                    <p>{checkItem[0].material["washingInstruction"]}</p>
                    <p className="red-text">{checkItem[0].material["warning"]}</p>
                </div>
            </div>
        </div>
    )
}

const mapStateToProps = (state, ownProps) => {
    const productId = ownProps.match.params.pid;
    return{
        checkItem: state.Products.filter(list => list.id === +productId)
    }
}

const mapDispatchToProps = (dispatch) => {
    return{
        addCart: (checkItem) => dispatch({type: "ADD_TO_CART", payload: checkItem})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SingleItem);
